#!/bin/bash

# Check if both host and port arguments are provided
if [ $# -ne 2 ]; then
    echo "Usage: $0 <host> <port>"
    exit 1
fi

HOST=$1
PORT=$2

# The GET request from the input
REQUEST="GET /? HTTP/1.1
Host: $HOST
Connection: close

"

# Send the request using netcat
echo -e "$REQUEST" | nc $HOST $PORT

echo "Request sent to $HOST:$PORT"
